jQuery('#memberRegister').submit(function(e){
    e.preventDefault();
    jQuery('.reg-er').html('');
    jQuery.ajax({
        url:'register-member',
        data: jQuery('#memberRegister').serialize(),
        type: 'post',
        success: function(result){
            if(result.status == "error"){
                jQuery.each(result.error, function(key, val){
                    jQuery('#'+key+'_error').html(val[0]);
                });
            }

            if(result.status == "success"){
                jQuery('#memberRegister')[0].reset();
                jQuery('#memberThanks').html(result.message);
            }
        }
    });
});

jQuery('#memberLogin').submit(function(e){
    jQuery('#login_message').html('');
    e.preventDefault();
    jQuery.ajax({
        url:'login-member',
        data: jQuery('#memberLogin').serialize(),
        type: 'post',
        success: function(result){
            if(result.status == "error"){
                jQuery('#login_message').html(result.message);
            }

            if(result.status == "success"){
                window.location.href="dashboard";
            }
        }
    });
});